+        private void writeToSlow(BufWriterImpl pool) {
+            for (int i = 0; i < charLen; ++i) {
+                char c = stringValue.charAt(i);
+                if (c >= '\001' && c <= '\177') {
+                    // Optimistic writing -- hope everything is bytes
+                    // If not, we bail out, and alternate path patches the length
+                    pool.writeU1((byte) c);
+                }
