+    private static int nextNonZeroInt() {
+        int i;
+        do {
+            i = RANDOM.nextInt();
+        } while (i == 0);
+        return i;
+    }
+
+    private static long nextNonZeroLong() {
+        long i;
+        do {
+            i = RANDOM.nextLong();
+        } while (i == 0);
+        return i;
+    }
