+    // Directly forward SRC from following pattern to multiplier input
+    //    AndV SRC (<=4294967295L)
+    auto forward_masked_input = [](Node* n) {
+      if (n->Opcode() == Op_AndV) {
+        return n->in(1)->Opcode() == Op_Replicate ? n->in(2) : n->in(1);
+      }
+      return n;
