+
+    public static void main(String[] args) throws Exception {
+        Robot robot = new Robot();
+        try {
+            EventQueue.invokeAndWait(AddRemoveMenuBarTest_5::initAndShowGui);
+            robot.waitForIdle();
+            robot.delay(500);
