+            EventQueue.invokeAndWait(() -> {
+                checkSize();
+                changeMenubar(false);
+            });
+            robot.waitForIdle();
+            robot.delay(500);
+
+            EventQueue.invokeAndWait(AddRemoveMenuBarTest_5::checkSize);
+        } finally {
+            EventQueue.invokeAndWait(frame::dispose);
+        }
+    }
+
+    public static void initAndShowGui() {
+        frame = new Frame();
+        frame.setLocationRelativeTo(null);
