+        if (d.width + vert > m.width || isScroll(false, m.width - vert, 0, d.width)) {
+            horz = H_HEIGHT;
+        }
+        if (d.height + horz > m.height || isScroll(true, m.height - horz, 0, d.height)) {
+            vert = V_WIDTH;
+        }
+        if (d.width + vert > m.width || isScroll(false, m.width - vert, 0, d.width)) {
+            horz = H_HEIGHT;
+        }
