+            put("KeyPairGenerator.Ed25519", EdDSAKeyPairGenerator.Ed25519.class.getName());
+            put("KeyPairGenerator.Ed448", EdDSAKeyPairGenerator.Ed448.class.getName());
+            put("KeyFactory.EdDSA", EdDSAKeyFactory.class.getName());
