+  }
+
+  if (strcmp(ShenandoahGCHeuristics, "aggressive") == 0) {
+    return new ShenandoahAggressiveHeuristics(space_info);
+  } else if (strcmp(ShenandoahGCHeuristics, "static") == 0) {
