+    return new ShenandoahCompactHeuristics(space_info);
+  } else {
+    vm_exit_during_initialization("Unknown -XX:ShenandoahGCHeuristics option");
+  }
+
+  ShouldNotReachHere();
+  return nullptr;
+}
