+
+  static const char* gc_mode_name(GCMode mode);
+
+  void notify_control_thread();
+
+  void service_concurrent_cycle(ShenandoahHeap* heap,
+                                ShenandoahGeneration* generation,
+                                GCCause::Cause &cause,
+                                bool do_old_gc_bootstrap);
+
+};
