-
-    NULL_CHECK(printHelp = (*env)->GetStaticMethodID(env, cls,
-                                        "printHelpMessage", "(Z)V"));
-
-    NULL_CHECK(jprogname = (*env)->NewStringUTF(env, _program_name));
