+        Instant expected, test;
+        long diff;
+        do {
+            expected = Instant.now(Clock.systemUTC());
+            test = Instant.now();
+            diff = Math.abs(test.toEpochMilli() - expected.toEpochMilli());
+        } while( diff > 100 ); // retry if more than 0.1 sec
