-                            // get URL with meaningful host component
-                            URL hostURL = url;
-                            for (URLConnection conn = urlConnection;
-                                 conn instanceof JarURLConnection;)
-                            {
-                                hostURL =
-                                    ((JarURLConnection) conn).getJarFileURL();
-                                conn = hostURL.openConnection();
-                            }
-                            String host = hostURL.getHost();
-                            if (host != null &&
