-        try {
-            Class<?>[] members = c.getNestMembers();
-            throw new Error("getNestMembers succeeded for " + c.getName());
-        } catch (SecurityException e) {
-            System.out.println("OK - getNestMembers for " + c.getName() +
-                               " got expected exception: " + e);
-        }
-    }
-
-}

