-    private static class MySecurityManager extends SecurityManager {
-        @Override
-        public void checkPermission(Permission perm) {
-            if (ACCESS_CLIPBOARD.equals(perm.getName())) {
-                isCheckPermissionCalled = true;
-            }
-        }
-    }
-}

