-
-        if (args.length == 0) {
-            String policy = Path.of(
-                    System.getProperty("test.src"), "flag.policy").toString();
-
-            // effectively true
-            Proc.create("Flag")
-                    .prop("java.security.manager", "")
-                    .prop("java.security.policy", policy)
-                    .prop("jdk.io.permissionsUseCanonicalPath", "true")
