-static void test_for_live_c_heap_block(size_t sz, ssize_t offset) {
-  char* c = NEW_C_HEAP_ARRAY(char, sz, mtTest);
-  LOG_HERE("C-block starts " PTR_FORMAT ", size %zu.", p2i(c), sz);
-  memset(c, 0, sz);
