@@ -284,0 +284,16 @@
+    int step(int range) {
+        if (randomized) {
+            l = scramble(l);
+        } else {
+            l++;
+        }
+        return (l & Integer.MAX_VALUE) % range;
+    }
+
+    static final int scramble(int n) {
+        int x = n;
+        x ^= x << 13;
+        x ^= x >>> 17;
+        x ^= x << 5;
+        return x == 0 ? 1 : x;
+    }
