+        var tool = ToolProvider.findFirst("jar").orElseThrow();
+        var args = cmdline.split(" +");
+        var code = tool.run(System.out, System.err, args);
+        if (code == 0) return;
+        throw new RuntimeException("jar failed with non-zero error code: " + code);
