@@ -684,0 +684,11 @@
+void MacroAssembler::add2reg_32(Register r1, int64_t imm) {
+  assert(Immediate::is_simm32(imm), "probably an implicit conversion went wrong");
+
+  if (Immediate::is_simm16(imm)) {
+    z_ahi(r1, imm);
+    return;
+  }
+  // imm is simm32
+  z_afi(r1, imm);
+}
