-                        pb.environment().merge("LD_PRELOAD", libjsig().toString(),
-                                (x, y) -> y + File.pathSeparator + x);
-                    }
+                boolean useLibjsig = scenario.endsWith("#libjsig");
+                if (useLibjsig) {
