+        final String type;
+        if (this instanceof HeapMemorySegmentImpl) {
+            type = "heap";
+        } else if (this instanceof MappedMemorySegmentImpl) {
+            type = "mapped";
+        } else {
+            type = "native";
+        }
+        return "MemorySegment{ kind: " +
+                type +
+                heapBase().map(hb -> ", heapBase: " + hb).orElse("") +
+                ", address: " + Utils.toHexString(address()) +
