@@ -1412,0 +1412,8 @@
+  int base_offset1 = arrayOopDesc::base_offset_in_bytes(T_BYTE);
+  int base_offset2 = arrayOopDesc::base_offset_in_bytes(T_CHAR);
+
+  assert((base_offset1 % (UseCompactObjectHeaders ? 4 :
