+    // Factories and helper methods
+
+    static Stream<CarrierLocalArenaPools> pools() {
+        return Stream.of(
+                CarrierLocalArenaPools.create(POOL_SIZE),
+                CarrierLocalArenaPools.create(POOL_SIZE, 16),
