-      // Simple recursive lock?
-      __ cmpptr(Address(rbp, lock_slot_rbp_offset), NULL_WORD);
-      __ jcc(Assembler::notEqual, not_recur);
-      __ dec_held_monitor_count();
-      __ jmpb(fast_done);
-      __ bind(not_recur);
