-  __ flush();
-
-  nmethod *nm = nmethod::new_native_nmethod(method,
-                                            compile_id,
-                                            masm->code(),
-                                            vep_offset,
-                                            frame_complete,
-                                            stack_slots / VMRegImpl::slots_per_word,
-                                            (is_static ? in_ByteSize(klass_offset) : in_ByteSize(receiver_offset)),
