-static void jfr_prologue(address the_pc, MacroAssembler* masm) {
-  Register java_thread = rdi;
-  __ get_thread(java_thread);
-  __ set_last_Java_frame(java_thread, rsp, rbp, the_pc, noreg);
-  __ movptr(Address(rsp, 0), java_thread);
-}
