-  const Register tbl = rdi;  // scratch
-
-  // Arguments are reversed on java expression stack
-  __ movl(val, Address(rsp,   wordSize)); // byte value
-  __ movl(crc, Address(rsp, 2*wordSize)); // Initial CRC
