-// Offset hacking within calls.
-static int pre_call_resets_size() {
-  int size = 0;
-  Compile* C = Compile::current();
-  if (C->in_24_bit_fp_mode()) {
-    size += 6; // fldcw
-  }
-  if (VM_Version::supports_vzeroupper()) {
-    size += 3; // vzeroupper
-  }
-  return size;
-}
