-    assert((dst_first & 1) == 0 && dst_first + 1 == dst_second, "invalid register pair");
-    int offset = ra_->reg2offset(dst_first);
-    if (masm != nullptr) {
-      __ kmov(Address(rsp, offset), as_KRegister(Matcher::_regEncode[src_first]));
