-    emit_opcode(masm,0xD9);            // FLDCW   std/24-bit mode
-    emit_opcode(masm,0x2D);
-    emit_d32( masm, Compile::current()->in_24_bit_fp_mode()
-        ? (int)StubRoutines::x86::addr_fpu_cntrl_wrd_24()
-        : (int)StubRoutines::x86::addr_fpu_cntrl_wrd_std());
