-    // Load the converted int; adjust CPU stack
-    emit_opcode(masm,0x58);       // POP EAX
-    emit_opcode(masm,0x3D);       // CMP EAX,imm
-    emit_d32   (masm,0x80000000); //         0x80000000
-    emit_opcode(masm,0x75);       // JNE around_slow_call
