-//----------Conditional Branch Operands----------------------------------------
-// Comparison Op  - This is the operation of the comparison, and is limited to
-//                  the following set of codes:
-//                  L (<), LE (<=), G (>), GE (>=), E (==), NE (!=)
-//
-// Other attributes of the comparison, such as unsignedness, are specified
-// by the comparison instruction that sets a condition code flags register.
-// That result is represented by a flags operand whose subtype is appropriate
-// to the unsignedness (etc.) of the comparison.
-//
-// Later, the instruction which matches both the Comparison Op (a Bool) and
-// the flags (produced by the Cmp) specifies the coding of the comparison op
-// by matching a specific subtype of Bool operand below, such as cmpOpU.
-
-// Comparison Code
-operand cmpOp() %{
-  match(Bool);
-
-  format %{ "" %}
-  interface(COND_INTER) %{
-    equal(0x4, "e");
-    not_equal(0x5, "ne");
-    less(0xC, "l");
-    greater_equal(0xD, "ge");
-    less_equal(0xE, "le");
-    greater(0xF, "g");
-    overflow(0x0, "o");
-    no_overflow(0x1, "no");
-  %}
-%}
-
-// Comparison Code, unsigned compare.  Used by FP also, with
-// C2 (unordered) turned into GT or LT already.  The other bits
-// C0 and C3 are turned into Carry & Zero flags.
-operand cmpOpU() %{
-  match(Bool);
-
-  format %{ "" %}
-  interface(COND_INTER) %{
-    equal(0x4, "e");
-    not_equal(0x5, "ne");
-    less(0x2, "b");
-    greater_equal(0x3, "nb");
-    less_equal(0x6, "be");
-    greater(0x7, "nbe");
-    overflow(0x0, "o");
-    no_overflow(0x1, "no");
-  %}
-%}
-
-// Floating comparisons that don't require any fixup for the unordered case
-operand cmpOpUCF() %{
-  match(Bool);
-  predicate(n->as_Bool()->_test._test == BoolTest::lt ||
-            n->as_Bool()->_test._test == BoolTest::ge ||
-            n->as_Bool()->_test._test == BoolTest::le ||
-            n->as_Bool()->_test._test == BoolTest::gt);
-  format %{ "" %}
-  interface(COND_INTER) %{
-    equal(0x4, "e");
-    not_equal(0x5, "ne");
-    less(0x2, "b");
-    greater_equal(0x3, "nb");
-    less_equal(0x6, "be");
-    greater(0x7, "nbe");
-    overflow(0x0, "o");
-    no_overflow(0x1, "no");
-  %}
-%}
-
-
-// Floating comparisons that can be fixed up with extra conditional jumps
-operand cmpOpUCF2() %{
-  match(Bool);
-  predicate(n->as_Bool()->_test._test == BoolTest::ne ||
-            n->as_Bool()->_test._test == BoolTest::eq);
-  format %{ "" %}
-  interface(COND_INTER) %{
-    equal(0x4, "e");
-    not_equal(0x5, "ne");
-    less(0x2, "b");
-    greater_equal(0x3, "nb");
-    less_equal(0x6, "be");
-    greater(0x7, "nbe");
-    overflow(0x0, "o");
-    no_overflow(0x1, "no");
-  %}
-%}
-
-// Comparison Code for FP conditional move
-operand cmpOp_fcmov() %{
-  match(Bool);
-
-  predicate(n->as_Bool()->_test._test != BoolTest::overflow &&
-            n->as_Bool()->_test._test != BoolTest::no_overflow);
-  format %{ "" %}
-  interface(COND_INTER) %{
-    equal        (0x0C8);
-    not_equal    (0x1C8);
-    less         (0x0C0);
-    greater_equal(0x1C0);
-    less_equal   (0x0D0);
-    greater      (0x1D0);
-    overflow(0x0, "o"); // not really supported by the instruction
-    no_overflow(0x1, "no"); // not really supported by the instruction
-  %}
-%}
-
-// Comparison Code used in long compares
-operand cmpOp_commute() %{
-  match(Bool);
-
-  format %{ "" %}
-  interface(COND_INTER) %{
-    equal(0x4, "e");
-    not_equal(0x5, "ne");
-    less(0xF, "g");
-    greater_equal(0xE, "le");
-    less_equal(0xD, "ge");
-    greater(0xC, "l");
-    overflow(0x0, "o");
-    no_overflow(0x1, "no");
-  %}
-%}
-
-// Comparison Code used in unsigned long compares
-operand cmpOpU_commute() %{
-  match(Bool);
-
-  format %{ "" %}
-  interface(COND_INTER) %{
-    equal(0x4, "e");
-    not_equal(0x5, "ne");
-    less(0x7, "nbe");
-    greater_equal(0x6, "be");
-    less_equal(0x3, "nb");
