-instruct countLeadingZerosI(rRegI dst, rRegI src, eFlagsReg cr) %{
-  predicate(UseCountLeadingZerosInstruction);
-  match(Set dst (CountLeadingZerosI src));
-  effect(KILL cr);
-
-  format %{ "LZCNT  $dst, $src\t# count leading zeros (int)" %}
