-  predicate( UseSSE<=1 );
-  match(Set src2 (SubD (MulD src0 src1) src2));
-  format %{ "FLD    $src0\t# ===MACRO3d===\n\t"
-            "DMUL   ST,$src1\n\t"
-            "DSUBRp $src2,ST" %}
-  ins_cost(250);
-  ins_encode( Push_Reg_FPR(src0),
-              FMul_ST_reg(src1),
-              Opcode(0xDE), Opc_plus(0xE0,src2));
-  ins_pipe( fpu_reg_reg_reg );
-%}
