+        for (int i = INDEX; i < SIZE; i++) {
+            assertEquals(i, lazy.indexOf(i));
+        }
+        assertEquals(-1, lazy.indexOf(SIZE + 1));
+    }
+
+    @Test
+    void lastIndex() {
+        var lazy = newList();
+        for (int i = INDEX; i < SIZE; i++) {
+            assertEquals(i, lazy.lastIndexOf(i));
+        }
+        assertEquals(-1, lazy.lastIndexOf(SIZE + 1));
+    }
+
+    @Test
