+        assertEquals(supplier.get(), cached.get());
+        assertEquals(1, cs.cnt());
+        assertEquals(supplier.get(), cached.get());
+        assertEquals(1, cs.cnt());
+        assertEquals("StableSupplier[value=[" + supplier.get() + "], original=" + cs + "]", cached.toString());
+    }
+
