+        assertThrows(UnsupportedOperationException.class, cached::get);
+        assertEquals(1, cs.cnt());
+        assertThrows(UnsupportedOperationException.class, cached::get);
+        assertEquals(2, cs.cnt());
+        assertEquals("StableSupplier[value=.unset, original=" + cs + "]", cached.toString());
+    }
