+    @Test
+    void recursiveCall() {
+        StableValue<Integer> stable = StableValue.of();
+        AtomicReference<StableValue<Integer>> ref = new AtomicReference<>(stable);
+        assertThrows(IllegalStateException.class, () ->
+                stable.orElseSet(() -> {
