+#ifndef SHARE_OOPS_UNLOADABLE_METHOD_HANDLE_INLINE_HPP
+#define SHARE_OOPS_UNLOADABLE_METHOD_HANDLE_INLINE_HPP
+
+#include "unloadableMethodHandle.hpp"
+
+#include "memory/universe.hpp"
+#include "oops/klass.inline.hpp"
+#include "oops/oopHandle.inline.hpp"
+#include "oops/weakHandle.inline.hpp"
+#include "runtime/javaThread.inline.hpp"
+
+inline UnloadableMethodHandle::UnloadableMethodHandle() :
+  _spin_lock(0) DEBUG_ONLY(COMMA _spin_lock_owner(nullptr)), _method(nullptr) {
+  set_state(EMPTY);
+}
+
+inline UnloadableMethodHandle::UnloadableMethodHandle(Method* method) :
+  _spin_lock(0) DEBUG_ONLY(COMMA _spin_lock_owner(nullptr)), _method(method) {
+  assert(method != nullptr, "Should be");
+
+  oop obj = get_unload_blocker(method);
+  if (obj != nullptr) {
+    _weak_handle = WeakHandle(Universe::vm_weak(), obj);
+    set_state(WEAK);
+  } else {
+    set_state(STRONG);
+  }
+  assert(is_safe(), "Should be");
+}
+
+inline UnloadableMethodHandle::State UnloadableMethodHandle::get_state() const {
+  return Atomic::load_acquire(&_state);
+}
+
+inline void UnloadableMethodHandle::set_state(State s) {
+  Atomic::release_store_fence(&_state, s);
+}
+
+inline void UnloadableMethodHandle::spin_lock() {
+  assert(_spin_lock_owner != Thread::current(), "Re-entering already owned lock, about to deadlock");
+  Thread::SpinAcquire(&_spin_lock);
+  DEBUG_ONLY(_spin_lock_owner = Thread::current();)
+}
+
+inline void UnloadableMethodHandle::spin_unlock() {
+  DEBUG_ONLY(_spin_lock_owner = nullptr;)
+  Thread::SpinRelease(&_spin_lock);
+}
+
+oop UnloadableMethodHandle::get_unload_blocker(Method* method) {
+  assert(method != nullptr, "Should be");
+
+  InstanceKlass* holder = method->method_holder();
+  if (holder->class_loader_data()->is_permanent_class_loader_data()) {
+    // Method holder class cannot be unloaded.
+    return nullptr;
+  }
+
+  // Return the holder that would block unloading.
+  // This would be either classloader oop for non-hidden classes,
+  // or Java mirror oop for hidden classes.
+  oop klass_holder = holder->klass_holder();
+  assert(klass_holder != nullptr, "Should be");
+  return klass_holder;
+}
+
+void UnloadableMethodHandle::release() {
+  switch (get_state()) {
+    case RELEASED: {
+      // Nothing to do.
+      break;
+    }
+    case EMPTY: {
+      set_state(RELEASED);
+      break;
+    }
+    case STRONG:
+    case WEAK: {
+      // Release only once.
+      // This also coordinates with is_safe check.
+      SpinLocker locker(this);
+      if (get_state() == STRONG || get_state() == WEAK) {
+        _strong_handle.release(Universe::vm_global());
+        _weak_handle.release(Universe::vm_weak());
+        _method = nullptr;
+        set_state(RELEASED);
+      }
+      break;
+    }
+    default:
+      ShouldNotReachHere();
+  }
+
+  assert(!is_safe(), "Should not be");
+}
+
+bool UnloadableMethodHandle::is_safe() {
+  switch (get_state()) {
+    case EMPTY:
+    case STRONG: {
+      // Definitely safe.
+      return true;
+    }
+    case RELEASED: {
+      // Definitely unsafe.
+      return false;
+    }
+    case WEAK: {
+      // Safety 1: Caller should be a Java thread in proper state.
+      // Otherwise, unloading can happen without coordinating with this thread.
+      // (Access API would assert this too, but do not rely on it.)
+      Thread* t = Thread::current();
+      if (!t->is_Java_thread() || JavaThread::cast(t)->thread_state() == _thread_in_native) {
+        // Unable to figure out safety, give a pessimistic answer.
+        assert(false, "Cannot figure out safety, check the thread lifecycle, or use method_unsafe()");
+        return false;
+      }
+
+      // Safety 2: Need to take a lock to coordinate with concurrent
+      // release that would change the weak handle state.
+      SpinLocker locker(this);
+      if (get_state() != WEAK) {
+        // State changed. Circle back to act accordingly.
+        return is_safe();
+      }
+
+      // Finally, see if the handle was cleared by GC.
+      return _weak_handle.peek() != nullptr;
+    }
+    default:
+      ShouldNotReachHere();
+      return false;
+  }
+}
+
+inline void UnloadableMethodHandle::make_always_safe() {
+  assert(is_safe(), "Should be");
+
+  switch (get_state()) {
+    case EMPTY:
+    case STRONG:
+    case RELEASED: {
+      // No action is needed.
+      break;
+    }
+    case WEAK: {
+      // Transition WEAK -> STRONG only once.
+      // Otherwise, this leaks strong handles.
+      SpinLocker locker(this);
+      if (get_state() == WEAK) {
+        oop obj = get_unload_blocker(_method);
+        assert(obj != nullptr, "Should have one");
+        _strong_handle = OopHandle(Universe::vm_global(), obj);
+        set_state(STRONG);
+      }
+      break;
+    }
+    default:
+      ShouldNotReachHere();
+  }
+
+  assert(is_safe(), "Should be");
+}
+
+inline Method* UnloadableMethodHandle::method() {
+  assert(is_safe(), "Should be");
+  return _method;
+}
+
+inline Method* UnloadableMethodHandle::method_unsafe() {
+  return _method;
+}
