+  //
+  // 2. recreate encoder if necessary
+  //
+  jboolean forceUpdate = JNI_FALSE;
+#ifdef ALWAYS_UPDATE_ENCODER_STATES
+  forceUpdate = JNI_TRUE;
+#endif // ALWAYS_UPDATE_ENCODER_STATES
