+id<MTLTexture>
+MTLTR_GetGlyphCacheTexture()
+{
+    J2dTraceLn(J2D_TRACE_INFO, "MTLTR_GetGlyphCacheTexture");
+    if (glyphCacheAA != NULL) {
+        return glyphCacheAA->texture;
+    }
+    return NULL;
+}
