+- (instancetype)init {
+    self = [super init];
+    if (self) {
+        self.available = nil;
+        self.availableTail = nil;
+        self.occupied = nil;
+        _lock = [[NSLock alloc] init];
+    }
+    return self;
+}
+
+- (void)occupyItem:(MTLTexturePoolItem *)item {
+    if (item.isBusy) return;
+    [item retain];
+    if (item.prev == nil) {
+        self.available = item.next;
+        if (item.next) {
+            item.next.prev = nil;
+        } else {
+            self.availableTail = item.prev;
+        }
+    } else {
+        item.prev.next = item.next;
+        if (item.next) {
+            item.next.prev = item.prev;
+        } else {
+            self.availableTail = item.prev;
+        }
+        item.prev = nil;
+    }
+    if (occupied) occupied.prev = item;
+    item.next = occupied;
+    self.occupied = item;
+    [item release];
+    item.isBusy = YES;
+}
+
+- (void)releaseItem:(MTLTexturePoolItem *)item {
+    [_lock lock];
+    @try {
+        if (!item.isBusy) return;
+        [item retain];
+        if (item.prev == nil) {
+            self.occupied = item.next;
+            if (item.next) item.next.prev = nil;
+        } else {
+            item.prev.next = item.next;
+            if (item.next) item.next.prev = item.prev;
+            item.prev = nil;
+        }
+        if (self.available) {
+            self.available.prev = item;
+        } else {
+            self.availableTail = item;
+        }
+        item.next = self.available;
+        self.available = item;
+        item.isBusy = NO;
+        [item release];
+    } @finally {
+        [_lock unlock];
+    }
+}
+
+- (void)addOccupiedItem:(MTLTexturePoolItem *)item {
+    if (self.occupied) self.occupied.prev = item;
+    item.next = self.occupied;
+    item.isBusy = YES;
+    self.occupied = item;
+}
+
+- (void)removeAvailableItem:(MTLTexturePoolItem*)item {
+    [item retain];
+    if (item.prev == nil) {
+        self.available = item.next;
+        if (item.next) {
+            item.next.prev = nil;
+            item.next = nil;
+        } else {
+            self.availableTail = item.prev;
+        }
+    } else {
+        item.prev.next = item.next;
+        if (item.next) {
+            item.next.prev = item.prev;
+            item.next = nil;
+        } else {
+            self.availableTail = item.prev;
+        }
+    }
+    [item release];
+}
+
+- (void)removeAllItems {
+    MTLTexturePoolItem *cur = self.available;
+    while (cur != nil) {
+        cur = cur.next;
+        self.available = cur;
+    }
+    cur = self.occupied;
+    while (cur != nil) {
+        cur = cur.next;
+        self.occupied = cur;
+    }
+    self.availableTail = nil;
+}
