+    private static Class<?> createWeakClass() {
+        var bytes = ClassFile.of().build(ClassDesc.of("WeakTest"), _ -> {});
+        try {
+            return MethodHandles.lookup().defineHiddenClass(bytes, true).lookupClass();
+        } catch (IllegalAccessException ex) {
+            return fail(ex);
+        }
+    }
