+        AbstractMemorySegmentImpl amsi = (AbstractMemorySegmentImpl) bytes;
+        if (amsi.unsafeGetBase() instanceof byte[] ba) {
+            if (amsi.unsafeGetOffset() == 0 && amsi.byteSize() == ba.length) {
+                return parse(ba);
+            }
+        }
+        // must copy the bytes
+        return parse(bytes.toArray(ValueLayout.JAVA_BYTE));
+    }
