+    // meta data for upgradable files
+    private static final String UPGRADEABLE_FILES_PATTERN = "jdk/tools/jlink/internal/runtimelink/upgrade_files_%s.properties";
+    private static final Module JDK_JLINK_MOD = LinkableRuntimeImage.class.getModule();
