+        Arrays.fill(allTrue, true);
+        A a = new A();
+        boolean[] allFalse = new boolean[3];
+        for (int i = 0; i < 20_000; i++) {
+            a.field1 = 0;
+            test1(a, allTrue);
+            test1(a, allFalse);
+            if (a.field1 != 42) {
+                throw new RuntimeException("Lost Store");
+            }
+        }
+    }
+
+    private static void test1(A otherA, boolean[] flags) {
+        otherA.field1 = 42;
