@@ -761,10 +761,29 @@
-  int limit = 3;
-  do {
-    thread_handle =
-      (HANDLE)_beginthreadex(nullptr,
-                             (unsigned)stack_size,
-                             &thread_native_entry,
-                             thread,
-                             initflag,
-                             &thread_id);
-  } while (thread_handle == nullptr && errno == EAGAIN && limit-- > 0);
+  {
+    int trials_remaining = 3;
+    DWORD next_delay_ms = 1;
+    while (true) {
+      thread_handle =
+        (HANDLE)_beginthreadex(nullptr,
+                              (unsigned)stack_size,
+                              &thread_native_entry,
+                              thread,
+                              initflag,
+                              &thread_id);
+
+      if (thread_handle != nullptr) {
+        break;
+      }
+
+      if (errno != EAGAIN) {
+        break;
+      }
+
+      if (trials_remaining-- <= 0) {
+        break;
+      }
+
+      log_debug(os, thread)("Failed to start native thread (%s), retrying after %dms.", os::errno_name(errno), next_delay_ms);
+      Sleep(next_delay_ms);
+      next_delay_ms *= 2;
+    }
+  }
