+    @Test
+    public int testRandomLimits(long x, long y) {
+        x = RANGE_1.clamp(x);
+        y = RANGE_2.clamp(y);
+        long z = x % y;
+
+        int sum = 0;
+        if (z < LIMIT_1) sum += 1;
+        if (z < LIMIT_2) sum += 2;
+        if (z < LIMIT_3) sum += 4;
+        if (z < LIMIT_4) sum += 8;
+        if (z > LIMIT_5) sum += 16;
+        if (z > LIMIT_6) sum += 32;
+        if (z > LIMIT_7) sum += 64;
+        if (z > LIMIT_8) sum += 128;
+
+        return sum;
+    }
