+  Node* lea_address = lea_derived_oop->in(AddPNode::Address);
+  Node* decode_address = decode->in(1);
+
+  bool is_spill = lea_address != decode_address &&
+                  lea_address->is_SpillCopy() &&
+                  decode_address->is_SpillCopy();
